vec4 BLACK = vec4(0.0,0.0,0.0,0.0);
vec4 WHITE = vec4(1.0,1.0,1.0,0.0);
vec4 GREY = vec4(0.5,0.5,0.5,0.0);

vec4 RED = vec4(1.0,0.0,0.0,0.0);
vec4 GREEN = vec4(0.0,1.0,0.0,0.0);
vec4 BLUE = vec4(0.0,0.0,1.0,0.0);

vec4 CYAN = vec4(0.0,0.8,1.0,1.0);
vec4 MAGENTA = vec4(1.0,0.0,1.0,0.0);
vec4 YELLOW = vec4(1.0,1.0,0.0,0.0);

vec4 ORANGE = vec4(1.0,0.5,0.0,0.0);
vec4 VIOLET = vec4(0.6,0.0,1.0,0.0);
vec4 LGREEN = vec4(0.5,1.0,.5,0.0);
vec4 DGREEN = vec4(0.0,.6,0.0,0.0);


int gcd(float x, float y)
{
    int a = int(x);
    int b = int(y);
    int t;
    while (0!=b)
    {
     	t=b;
        b=a%b;
        a=t;
    }
    return a;
}

vec4 colorize(int i)
{
	//if (1==i)
    {
     //return BLACK;   
    }
    int j = i%10;
    switch (j)
    {
        case 1: return WHITE;
        case 2: return RED;
        case 3: return ORANGE;
        case 4: return YELLOW;
        case 5: return LGREEN;
        case 6: return DGREEN;
        case 7: return CYAN;
        case 8: return BLUE;
        case 9: return VIOLET;
        case 0: return MAGENTA;  
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 muv = iMouse.xy / iResolution.xy;
    float scale = clamp((muv.x)+0.1,0.0,1.0);

    float x = floor(fragCoord.x*scale);
    float y = floor(fragCoord.y*scale);




    int z = gcd(x,y);


    //tick marks on the edges every 10 squares
	if ((y==0.0 && mod(x,100.0)==0.0)||(x==0.0 && mod(y,100.0)==0.0))
    {
        fragColor = RED;
    }
    else if ((y==0.0 && mod(x,10.0)==0.0)||(x==0.0 && mod(y,10.0)==0.0))
    {
        fragColor = BLUE;
    }
    else if (y==0.0||x==0.0)
    {
     fragColor = BLACK;   
    }
	//else if (1 == z)
    //{
      // fragColor = BLACK;
   // }
	else
    {        

        //float w = 1.5-(1.0/muv.y)/float(z);
        float w = 100.0*muv.y;

        if (z>int(w))
		fragColor = colorize(z);



    }
}
