void mainImage( out vec4 C, in vec2 U )
{  
    //U.y *= 0.88;
    U *=  vec2(2,3) / iResolution.x; 
    //U =  U.xy / iResolution.xy * 2.0;
    U.y -=  iTime / 5.;
    float wy = cos(U.y * 12.);
    float wx = sin(U.x * 15.); 
    float w  = cos(U.x * 30.);
    C = vec4(smoothstep(-0.25, 0.25,mix(wy + wx, wx*1.4, w * 3.)));
}
