// edge detect kernel
const float
	k1 = -1.0, k2 = -1.0, k3 = -1.0,
	k4 = -1.0, k5 = 8.0,  k6 = -1.0,
	k7 = -1.0, k8 = -1.0, k9 = -1.0;

float sigmoid(float a, float f)
{
	return 1.0/(1.0+exp(-f*a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{		
	vec2 uv = fragCoord.xy / iResolution.xy;
	float mousedx = iMouse.x/iResolution.x/2.0;
	if (uv.x<0.5)
	{
		uv = vec2(uv.x+mousedx,uv.y);
		vec2 p = vec2(1.0,1.0) / iChannelResolution[0].xy;
		vec4 colorSum =
			texture(iChannel0, uv + p * vec2(-1.0,-1.0)) * k1 +
			texture(iChannel0, uv + p * vec2( 0.0,-1.0)) * k2 +
			texture(iChannel0, uv + p * vec2( 1.0,-1.0)) * k3 +
			texture(iChannel0, uv + p * vec2(-1.0, 0.0)) * k4 +
			texture(iChannel0, uv + p * vec2( 0.0, 0.0)) * k5 +
			texture(iChannel0, uv + p * vec2( 1.0, 0.0)) * k6 +
			texture(iChannel0, uv + p * vec2(-1.0, 1.0)) * k7 +
			texture(iChannel0, uv + p * vec2( 0.0, 1.0)) * k8 +
			texture(iChannel0, uv + p * vec2( 1.0, 1.0)) * k9 ;

		vec3 c = colorSum.rgb;
		float avg = (c.r+c.g+c.b)/3.0;
		float v = (1.0-sigmoid(avg-0.18, 9.0))*2.0;
		fragColor = vec4(v,v,v,1.0);
	}
	else
	{
		uv = vec2(uv.x-0.5+mousedx,uv.y);
		fragColor = texture(iChannel0, uv);
	}
}
