void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 color = vec4(0,0,0,1);
	vec2 uv = fragCoord.xy / iResolution.xy;

	color.x = texture(iChannel0,vec2(uv.x,0)).x;
	color.y =  texture(iChannel0,vec2(uv.x,0.7)).x;
	color.z = texture(iChannel0,vec2(uv.y,0)).x;

	fragColor = color;
}
