void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 col = vec4(vec3(cos(iTime)*.2+uv.x, sin(iTime)*.1+uv.y, 1.), 1.0);
	float dist = distance(vec2(uv.x, sin(uv.x*sin(iTime)*2.+iTime)/3.+.5), uv);
	col += vec4(vec3(exp(-pow(dist,2.)/2e-3)), 1.);

	fragColor = col;
}
