/*
	-----------------------
	Simple Difference Keyer
	-----------------------

    A really simple (not optimized) "Difference Keyer" with basic despill and blurred key.
    Just to play a little with GLSL shaders and video :o)

    The box blur used in this shader is from 4rknova (Thank you !!!) :
    https://www.shadertoy.com/view/4dlGDr

	Francois 'CoyHot' Grassard - 2014
*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const int samples = 10;
	const float desplillValue = 0.1;
	float rad = 0.02;


	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 fg = texture(iChannel0,uv);
	vec4 bg = texture(iChannel1,-uv);

	// Blurred image (using Box Blur from 4rknova ... thx !!!!)
	vec3 blurredImage = vec3(0.0);

	for (int i = -samples; i < samples; i++)
	{
		for (int j = -samples; j < samples; j++)
		{
			blurredImage += texture(iChannel0, uv + vec2(i, j) * (rad/float(samples))).xyz
				 / pow(float(samples) * 2.0, 2.0);
		}
	}

	// Simple difference keying between Green and Red
	vec4 rawKey = vec4 (vec3(blurredImage[1]-blurredImage[0]),1.0);

	// Normalize Key (not auto-detected but tweaked manually)
	vec4 normalizeKey = clamp((1.0-(rawKey*10.0)),0.0,1.0);

	// Despill  (by clamping green channel by the red one).
	fg.g = clamp (fg.g, 0.0, fg.r-desplillValue);


	fragColor = (normalizeKey * fg)+((1.0-normalizeKey) * bg);
}
