void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

	// get wave based on the uv map
	float wave1 = texture( iChannel0, vec2(uv.x,0.75) ).x;
	float wave2 = texture( iChannel0, vec2(uv.y,0.75) ).x;
	float wave3 = texture( iChannel0, vec2(uv.x / uv.y,0.75) ).x;

	// Create the pixel coler based on the 3 wave's
	vec3 col = vec3(wave1, wave2, wave3);

	// Clamp the color to remove the gray color and get some beter lines (This tuned for the 8 bit mentality song )
	col -= 0.35; 
	col = clamp(col, vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0));
	col *= 2.5; 

	fragColor = vec4(col,1.0);
}
