float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
	float r = rand(uv*iTime);
	vec4 VI = texture(iChannel0,uv);
	VI*=r; 
	fragColor = vec4(VI);
}
