void mainImage( out vec4 g, vec2 c ) {
    vec4 p = vec4(c,0,0)/iResolution.xyxy-.5, d=p, t;
 	p.z -= iTime*8.;
    for(float i = 0.; i < 1.; i += .001)
    {
        p += d*i;
        t = texture(iChannel0, p.xz * .001, p.x*.02) * p.x*.05;
        g = mix(t*t * (.8-i), d+1.+ d.y, i);
        if(t.x-1. > p.y*.05) break;
        g += max(2.-length(d.xy*10.-vec2(3,1)), 0.);
    }
}
