void mainImage( out vec4 O, vec2 I )
{
    O.xy=iResolution.xy; 
    O=vec4(I+I-O.xy,O.y,0);
    O/=length(O);
    float l=0.,f,g,i,j;
    for(i=1.;i++<9.;)
        for(j=0.;j++<i;){
            f=.2*i*(g=iTime)+6.*j/i;
            vec4 P = vec4(i*cos(f),i*sin(f),3.*cos(g)-7.,0);
           	l=(f=dot(O,P))*f-dot(P,P)+exp2(-i)>0.?-4.*(O*f-P).y:l;
    }
    O=vec4(l);
}
