// beginner, code is messy

//palette color
vec3 white = vec3(1.0,1.0,1.0);
vec3 black = vec3(0.0,0.0,0.0);
vec3 grey = vec3(0.4,0.4,0.4);
vec3 yellow= vec3(1.0,0.87,0.13);
vec3 brown = vec3(89.0,59.0,20.0)/255.0;
vec3 dark = vec3(47.0,29.0,10.0)/255.0;

//draw eye at position
vec3 drawEye(vec3 col, vec2 uv, vec2 position,float blinkOffset)
{
	uv-=position;
	float bl = smoothstep(0.6,1.0,pow(0.5+0.5*cos(iTime*2.0+blinkOffset),3.0));
	float bp = -1.0+2.0*smoothstep(-0.02,0.02,cos(iTime+0.5*3.14)+0.1);
	float p1 = length( uv-vec2(0.0,0.65));
	float p2 = length(uv-vec2(0.0,-0.15 - 9.2*bl));
	float p5 = length(uv-vec2(0.0, 1.3 + 9.2*bl));
	float p3 = length( uv-vec2(-0.01+0.1*bp,0.63));
	float p4 = length( uv-vec2(-0.07+0.1*bp,0.65));

	float c1= smoothstep(0.85,0.86,0.02+p2-bl*9.0);
	float c2= smoothstep(0.85,0.86,0.02+p5-bl*9.0);
	float c3= smoothstep(0.04,0.05,p3);
	float c4= smoothstep(0.09,0.1,p3);
	float c5= smoothstep(0.06,0.1,p3);
	float c6=smoothstep(0.24,0.25,p1);
	float c7=smoothstep(0.34,0.35,p1);
	float c8=smoothstep(0.015,0.025,p4);

	return mix(mix(mix(mix(mix(mix(white,mix(mix(black,brown,c3),dark,c5),c8),white,c4),yellow,c1),yellow,c2),grey,c6),col,c7);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float i = iResolution.x / iResolution.y;
	uv.x *=i;



	//canvas
	vec3 col= yellow;

	//ribbon
	float r = smoothstep(0.57,0.58,uv.y)-smoothstep(0.72,0.73,uv.y);
	col = mix(col,black,r);

	// left eye
	col = drawEye(col, uv, vec2(0.33*i,0.0), 0.0);

	//right eye
	col = drawEye(col, uv, vec2(0.67*i,0.0),0.05);

	// mouth
	float bm = -0.1*smoothstep(0.5,1.0,pow(0.5+0.5*cos(iTime*2.0+2.0*3.14),8.0));
	float p9 = length(uv-vec2(0.5*i,0.9+0.8*bm));
	float m= atan(uv.y-0.68,uv.x-0.52*i);
	float r5= (smoothstep(0.8+bm,0.81+bm,p9)-smoothstep(0.86+bm,0.87+bm,p9))*(1.0-smoothstep((-0.75+0.2*bm)*3.14,(-0.755+0.2*bm)*3.14,m)- smoothstep((-0.255-0.2*bm)*3.14,(-0.25-0.2*bm)*3.14,m));
	col = mix(col,black,r5);	
	//*(1.0-smoothstep(0.8*3.14,0.81*3.14,m)- smoothstep(0.21*3.14,0.2*3.14,m))
	fragColor = vec4(col,1.0);
}
