#define M_PI 3.14159265358979
float gauss(float x) {
    return exp(-(x*x)*20.); 
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float dim = min(iResolution.x, iResolution.y);
    float freq = mix(5., 30., iMouse.x/dim);
    vec2 uv = fragCoord.xy / dim;
    float g = gauss(uv.x - .5)*gauss(uv.y - .5);
    float s = .5*sin(freq*uv.x * 2. * M_PI);
    fragColor = vec4(.5 + vec3(g*s),1.0);
}
