const vec2 MED = vec2(0.5,0.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv - MED;
    float a = iTime; //1.570796326;//iTime; //0.35;
    float c = cos(a);
    float s = sin(a);
    vec2 fUV = vec2( p.x*c - p.y*s, p.x*s + p.y*c );
    vec4 cor = texture(iChannel0,MED+fUV);
	fragColor = cor + vec4(0.0,0.0,0.0,0.0); //vec4(uv,0.5+0.5*sin(iTime),1.0);
}
