float intensity = 1.12;
float rot = -15.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    uv.y = uv.y * -1.0; 
    vec3 col=texture(iChannel0,uv).rrr;

    col = vec3(col.r *.98, col.g, col.b *1.04)* 1.5;
    uv.y = uv.y * -1.0; 

    float ts=uv.y-.2+sin(uv.x*4.0+7.4*cos(uv.x*10.0))*0.005;
    col=mix(col,vec3(1.5* (.85+iTime* 0.02)-uv.y),smoothstep(0.45,0.0,ts));

    float c=cos(rot*0.01),si=sin(rot*0.01);
	uv=(uv-0.5)*mat2(c,si,-si,c);	

    float s=texture(iChannel1,uv * 1.01 +vec2(iTime)*vec2(0.02,0.501)).r;
    col=mix(col,vec3(1.0),smoothstep(0.9,1.0, s * .9 * intensity));

    s=texture(iChannel1,uv * 1.07 +vec2(iTime)*vec2(0.02,0.501)).r;
    col=mix(col,vec3(1.0),smoothstep(0.9,1.0, s * 1. * intensity));

    s=texture(iChannel1,uv+vec2(iTime)*vec2(0.05,0.5)).r;
    col=mix(col,vec3(1.0),smoothstep(0.9,1.0, s * .98 * intensity));

	s=texture(iChannel1,uv * .9 +vec2(iTime)*vec2(0.02,0.51)).r;
    col=mix(col,vec3(1.0),smoothstep(0.9,1.0, s * .99 * intensity));

	s=texture(iChannel1,uv * .75 +vec2(iTime)*vec2(0.07,0.493)).r;
    col=mix(col,vec3(1.0),smoothstep(0.9,1.0, s * 1. * intensity));

	s=texture(iChannel1,uv * .5 +vec2(iTime)*vec2(0.03,0.504)).r;
    col=mix(col,vec3(1.0),smoothstep(0.94,1.0, s * 1. * intensity));

	s=texture(iChannel1,uv * .3 +vec2(iTime)*vec2(0.02,0.497)).r;
    col=mix(col,vec3(1.0),smoothstep(0.95,1.0, s * 1. * intensity));

	s=texture(iChannel1,uv * .1 +vec2(iTime)*vec2(0.0,0.51)).r;
    col=mix(col,vec3(1.0),smoothstep(0.96,1.0, s * 1. * intensity));

	s=texture(iChannel1,uv * .03 +vec2(iTime)*vec2(0.0,0.523)).r;
    col=mix(col,vec3(1.0),smoothstep(0.99,1.0, s * 1. * intensity));

	fragColor = vec4(col,1.0);
}
