float PI = 3.14159265359;

float getAngle(vec2 uvCenter){
	float angle = atan(uvCenter.y,uvCenter.x )/(2.0*PI);
	if(angle <0.0) angle +=1.0;
	return angle;
}

float getDis(vec2 v){
	return sqrt(v.x* v.x + v.y*v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uvCenter = uv - vec2(0.5,0.5);
	float angle = getAngle(uvCenter) + iTime*.1;
	float dis = getDis(uvCenter) ;	
	dis += 1.0+ sin(angle*2.0*PI*10.0) *0.1;    
    float fft  = texture( iChannel0, vec2(uv.x,uv.y) ).x; 	
	vec3 col = vec3( sin(dis*PI*6.0)*fft, 4.0*cos(dis*PI -iTime*10.3)*fft*(1.0-fft), 1.0-fft*acos(dis*PI -iTime*10.3) ) * fft;
	fragColor = vec4(col,1.0);
}
