void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = 1.0-uv.y;
    fragColor = texture(iChannel0, uv);

    //get grey comp of ichannel1
    vec4 secondImageGrey = texture(iChannel1, uv);
    float grey = (secondImageGrey.r + secondImageGrey.g + secondImageGrey.b) / 3.0;
    secondImageGrey.r = grey;
    secondImageGrey.g = grey;
    secondImageGrey.b = grey;

    //threshold on sin time
    float thresh = (sin(iTime)*0.5) + 0.5;
    float thresh2 = (sin(iTime+0.1)*0.5) + 0.5;

    if(secondImageGrey.r > thresh2)
    {
        	fragColor.b = 0.0;
        	fragColor.r = 1.0;
        	fragColor.g = 0.5;
    }

    if(secondImageGrey.r > thresh)
    {

		fragColor.r = 0.0;
        fragColor.g = 0.0;
        fragColor.b = 0.0;
    }
}
