void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

   	float r = sqrt(pow(uv.x - 0.5, 2.0) + pow(uv.y - 0.5, 2.0));
    float phi = atan(uv.x - 0.5, uv.y - 0.5);

    float rn = 2.0 * pow(r, 2.5);

    uv.x = rn * cos(phi) + 0.5;
    uv.y = rn * sin(phi) + 0.5;

    vec4 fft = texture(iChannel0, vec2(abs(0.5 - uv.x), 0));
    vec4 wave = texture(iChannel0, vec2(uv.x, 1));

	vec4 col = vec4(uv,0.5+0.5*sin(iTime),1.0);

    col += texture(iChannel0, vec2(uv.x + 0.5 * uv.y, 0));

    col *= fft;

    fragColor = col;
}
