#define MAGIC_FORMULA pow(abs(sin(iTime+0.1*rand(uv + iTime))*1.002), 600.)

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
	vec2 uv = fragCoord.xy / iResolution.xy;


    // barrel distortion
    uv -= vec2(0.5);

    uv *= pow(length(uv), 0.15);

    uv += vec2(0.5);

    // fragment displacement (circular functions)
    uv.x += sin(uv.y*20.+iTime*10.)*0.01*MAGIC_FORMULA;

    uv.x += cos(uv.y*30.+iTime*10.)*0.01*MAGIC_FORMULA;

    uv.y += 0.2*MAGIC_FORMULA*sin(rand(vec2(iTime)));

    uv.y = mod(uv.y, 1.);

    // get texture data
    vec4 video_col = mix(texture(iChannel0, uv), vec4(0.7), 0.2*MAGIC_FORMULA);

    // dark bands
    video_col -= abs(sin(uv.y*100. + iTime*5.))*0.08;

    video_col -= abs(sin(uv.y*300. - iTime*10.))*0.05;

    // tv edges
    video_col -= vec4(pow(length(uv - vec2(0.5)), 0.5))*0.5;

    // signal noise
    video_col *= rand(uv + vec2(cos(iTime), sin(iTime)))*4.;

    // darken the final result a bit
    video_col -= 0.1;

    video_col += uv.y*vec4(0.5, 0.5, 1., 1.)/4.;
    video_col -= (1. - uv.y)*vec4(0.5, 1., 0.5, 1.)/8.;

    // send color to screen
	fragColor = video_col;
}

#define MAGIC_FORMULA pow(abs(sin(time+0.1*rand(vec2(time)))*1.002), 600.)

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float lerp(float a, float b, float t) {
    return clamp(0., 1., b * t + a * (1. - t));
}


vec2 mainSound( in int samp,float time)
{
    return clamp(0.05 + MAGIC_FORMULA,0.0,0.2)*vec2( rand(vec2(time,time)) )*lerp(1., 0., (time-55.)/5.);
}
