void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat2 ro = mat2(cos(iTime/8.0),sin(iTime/8.0),-sin(iTime/8.0),cos(iTime/8.0));
    vec2 uv = ro*(fragCoord.xy-iResolution.xy/2.0)+iResolution.xy/2.0;
    vec2 px = 2.0*(iResolution.xy-2.0*uv)/iResolution.xy;
    vec3 co = 0.5+0.5*(cos(iTime+4.0*px.x*px.y+vec3(3.142,1.571,0.0)));
    fragColor = vec4(mix(co.zxy,co.xyz,abs(sin(iTime*0.25))),0.0);
}
