void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y * 2.;

    float count = floor(20. * (sin(iTime * .5)*.5+.5));

    if (iMouse.z > 0.)
        count = floor(50.*iMouse.x/iResolution.x);

	float a = atan(p.x,p.y)/3.14159*count/2.;

    a = fract(a);

	float d = length(p) - 1. / (0.545 * sin(a) + cos(a));

	d = smoothstep(0.,1.,.5 - abs(d));

	//d = smoothstep(0.,0.01/d,d);

	fragColor = vec4(d);
}
