void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime*iTime;
    float zoom = iTime*1.5;
    float p = sin(uv.x*zoom)-sin(uv.y*zoom);
    float pOff = sin((uv.x-(sin(iTime)/100.0))*zoom)-sin(uv.y*zoom);
    pOff*=sin((uv.x-(sin(iTime)/100.0))*zoom)-sin(uv.x*zoom);
    float pIn = (-p)*1.0;
    float r,g,b=0.0;
    g=sin((pIn/2.0)+sin(iTime)*uv.x);
    r=(p-pIn)*g;

    b=((pIn*pOff)*3.0);


    if(r>=1.2){
        r=sin(zoom*5.0*uv.x-uv.y);
        //r=sin(r);
    }

   	b=cos(p);
    if(b>=.9){
		r=0.0;
        g=0.0;
        b=0.0;
    }
    g+=b;

	fragColor = vec4(r,g,b,1.0);
}
