// reference image: https://www.facebook.com/CosmosUpp/photos/a.219708114844645.1073741835.218946754920781/891758304306286

#define M(x) floor( 22.5* (x) ) / 22.5                        // pixelization
#define D(x) vec4(fract(1e4*sin(M(x))))                       // random grey

void mainImage( out vec4 O, vec2 U )
{
	vec2 V = U * 4./iResolution.y - vec2(.5,0), A;            // normalize screen coords * 4
    U = fract(V)*2.-1.;                                       // local cell coords
    V = floor(V);                                             // cell Id (for seeding)          

    if (abs(U.y) > M(.5)) 
         O = D( U.y + V.y );                                  // horizontal strips
    else {
        A = M(abs(U));                                        // rectangles
        float a = M( 1. - sqrt(.25-A.y*A.y) ),                // diag delimited by circles
              c = A.x < a  ? A.y : A.x;                       // horiz vs vertic part
        O = D( ( c==A.y && c>.25 ? a : c ) + V.y );
     // O = D( ( c==A.y && c>.25 ? a : c ) + V.y +17.*V.x );  // variant
    }
}
