void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy) / iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv -= ((iMouse.xy/iResolution.xy)-0.5)*2.0;
    float pi2 = 3.14159265 * 2.0;
    float distance = length(uv);
    float angle = atan(uv.y  , uv.x);
    float m1 = 0.6;
    float m2 = 0.4;
    float freq1 = 5.0;
    float freq2 = 200.0;

    float color0 = sin(distance * freq1 + angle - iTime * freq1);
    float color1 = sin(distance * freq1 + angle - iTime * freq1 + (1.0/3.0) * pi2);
    float color2 = sin(distance * freq1 + angle - iTime * freq1 + (2.0/3.0) * pi2);

    float color00 = sin(distance * freq2 + angle - iTime);
    float color11 = sin(distance * freq2 + angle - iTime + (1.0/3.0) * pi2);
    float color22 = sin(distance * freq2 + angle - iTime + (2.0/3.0) * pi2);

	fragColor = vec4(vec3(color00*m1 +color0*m2, color11*m1 + color1*m2, color22*m1 + color2*m2),1.0);
}
