const float thickness = 10.0;
const vec2 pointA = vec2(0.0, 0.0); //start point
const vec2 pointB = vec2(0.1, 0.3); //end point

float sdSegment( vec2 p, vec2 a, vec2 b ) {
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float px = 1.0 / iResolution.y;
    float lineThickness = px * thickness / 2.0;

    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;
    float d = sdSegment( uv, pointA, pointB );

    fragColor = mix( vec4(0.3, 0.7, 0.11,1.0), vec4(0.0), smoothstep( lineThickness-px, lineThickness+px, d) );
}
