void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      vec2 p = 3.0*gl_FragCoord.xy/vec2(iResolution);
    vec2 uv = gl_FragCoord.xy/vec2(iResolution);
   for(int n=1; n<10; n++)
   {
      float i = float(n);
      p += vec2(
        0.3/i*sin(i*p.y-p.x+iTime*1.0+12.0*i)-0.6,
        0.3/i*sin(i*p.x-p.y+iTime*1.0+12.0*i)-0.6
      );
   }


     float rot = radians(-45.0);

    p-=.1;

    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
   	p  = m * p;

    p+=.5;

    vec4 text = texture( iChannel0, uv ).rgba;
    if (text.a>0.0){
       fragColor =  vec4(0.5*sin(p.x)+0.9,0.0,cos(p.x+p.y)+0.9,text.a)*text;
    } else {
         fragColor =  vec4(0.0);

    }



}
