float blend(float lhs, float lhsAlpha, float rhs)
{
	return (lhs * lhsAlpha) + (rhs * (1.0-lhsAlpha));
}

float blend(float lhs, float lhsAlpha, float mid, float midAlpha, float rhs)
{
	return (lhs * lhsAlpha) + (mid * midAlpha) + (rhs * (1.0-lhsAlpha-midAlpha));
}

vec4 alphaBlend(vec4 lhs, vec4 rhs)
{
	vec4 res;
	res.r = blend(lhs.r, lhs.a, rhs.r);
	res.g = blend(lhs.g, lhs.a, rhs.g);
	res.b = blend(lhs.b, lhs.a, rhs.b);
	return res;
}

vec4 alphaBlend(vec4 lhs, vec4 mid, vec4 rhs)
{
	vec4 res;
	res.r = blend(lhs.r, lhs.a, mid.r, mid.a, rhs.r);
	res.g = blend(lhs.g, lhs.a, mid.g, mid.a, rhs.g);
	res.b = blend(lhs.b, lhs.a, mid.b, mid.a, rhs.b);
	return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//uv.y *= -1.0;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

	//fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
	//fragColor *= vec4(0.5,0.5,0.5,0.5);
	vec4 vid1 = texture(iChannel0, uv);
	vec4 vid2 = texture(iChannel1, uv);
	vec4 vid3 = texture(iChannel2, uv);
	vid1.a = abs(sin(iTime));
	//vid2.a = - vid1.a;
	vid2.a = abs(cos(iTime));
	vid3.a = (1.0 - vid1.a) - vid2.a;
	fragColor = (alphaBlend(vid1,vid2));
	//fragColor = (alphaBlend(vid1,vid2,vid3));
	fragColor = vec4(1,1,1,1) - fragColor;

}
