const float duration =4.; //sec
const float vbl_wait = 3.0; // wait a bit

void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;

    float scl = mod(U.y-iDate.w,1.0);

//    O = U.y < fract(iDate.w/duration) ? texture(iChannel0, U) : vec4(1);

      O = U.y < min(1., mod(iDate.w,duration+vbl_wait)/duration)
              ? 0.2-texture(iChannel0, U)+scl : vec4(0); 
}



