// Look at the Sound tab!

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(1.0);
}

// Emulating PC Speaker mode3 enviroment - code by TomCat/Abaddon
// you can use it as you want...

vec2 mainSound( in int samp, float time )
{
    // silent
    float amp = 0.;

    // PIT counter
    int counter = int(time*105./88.*1000000.);

	// 440Hz A4 pitch
    int divider = 2712;

    // mode3 square wave generator
    amp = float(2&int(4*counter/divider))-1.;

    return vec2( amp );
}
