#define L(s) length(max(abs(p) - s, 0.)) // box
#define T iTime

mat2 r(float a) {float c = cos(a), s = sin(a); return mat2(c, -s, s, c);} // rotate

void mainImage( out vec4 f, in vec2 g )
{
	vec2 R = iResolution.xy, p = (g+g-R)/R.y;
    sin(T*.5) > 0. ? p.x += (T) : p.y += T;
    p = mod(p, 1.5)-.75; // repetition
    p = abs(p);  // Mirror
    float s = 1., c = 0.; //size, color
    for (int i = 0; i < 20; i++)
        c += (smoothstep(L(s), .1, 0.) - smoothstep(L(s*.9), .23, 0.))*.5, //make box outline
        p *= r(3.1415 / 2. + T*.1),
        s *= .9, // decreaes size
        f += (c);
}
