#define petalCount 12.0 // it better be an even number (and float type)

float u( float x ) { return (x>0.0)?1.0:0.0; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 center = iResolution.xy/2.0;
	vec2 resolution = vec2(min(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y))*0.85;

	float time = iTime/1.4;
	vec2 p = 2.0 * (fragCoord.xy - center.xy) / resolution.xy;

	float a = atan(p.x,p.y);
	float r = length(p)*.75;

	float w = cos(3.1415927*time-r*2.0);
	float h = 0.5+0.5*cos(petalCount*a-w*7.0+r*8.0);
	float d = 0.25+0.75*pow(h,1.0*r)*(0.7+0.3*w);

	float col = u( d-r ) * sqrt(1.0-r/d)*r*2.5;
	col *= 1.25+0.25*cos((petalCount*a-w*7.0+r*8.0)/2.0);
	col *= 1.0 - 0.35*(0.5+0.5*sin(r*30.0))*(0.5+0.5*cos(petalCount*a-w*7.0+r*8.0));
	fragColor = vec4(
		col-h*r + 0.1*h*(1.0-r),
		col-h*0.5+r*.2 + 0.35*h*(1.0-r),
		col+h*r - 0.1*h*(1.0-r),
		1.0);
}
