void mainImage( out vec4 o, in vec2 I )
{
    I = 2.*I.xy / iResolution.xy-1.;
    float w1 = .4*sin(4.*I.x);
    float w2=.4*cos(13.*I.x);
    float w3=.4*sin(4.*I.x*I.x*3.);

    float y = w1+w2+w3-.105;

    float x = mod(iTime,2.)-1.;

    float r=.2;

    float f = 1.-22.*abs(y-I.y);
    float rf = 1.-10.*length(I-vec2(x,y))-r;

    vec3 col = vec3(f);
    col.r += rf;
    o = vec4(col,1.0);
}
