#define POS0 vec4(10.0, 30.0, 10.0, 2.0)
#define FREQ vec4(1.0, 0.8, 0.7, 1.0)

#define NCOLS 250.0

#define BLACK_COLOR vec4(0.0,0.0,0.0,1.0)

#define COLOR1 vec4(0.0,1.0,0.0,1.0)
#define COLOR2 vec4(0.0,0.0,1.0,1.0)
#define COLOR3 vec4(0.0,0.8,0.0,1.0)
#define COLOR4 vec4(0.0,0.0,0.8,1.0)

#define WHITE_COLOR vec4(1.0,1.0,1.0,1.0)


 float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 

	float patterWidth = iResolution.x / NCOLS;

	float nRows = float(int(iResolution.y / patterWidth)) + 1.0;

	float patterHeight = iResolution.y / nRows;

	int iCol = int(fragCoord.x / patterWidth);
	int iRow = int(fragCoord.y / patterHeight);

	vec2 coord = vec2(float(iCol),float(iRow));

	float i = float(iCol) + (float(iRow) * NCOLS);
	//float si = rand(vec2(float(iCol+1),float(iRow+1)));
	//float si = (1.0 + sin(i) + cos(2.0*i)) / 3.0;
 	//float si = 0.5;
	//float si = i / (nRows * NCOLS);

	vec2 center = vec2(NCOLS/2.0, nRows / 2.0);
	float si =   distance(center,coord) / distance(center,vec2(0.0,0.0));

	//float si = degrees(acos((coord.x-center.x)/ distance(center,coord))) / 180.0;

	float si2 = (si + (0.05*sin(0.2*iTime))) / 1.05;

	//si2 = si;

	vec4 timePattern = POS0 * si + (iTime * (FREQ * si2 + 1.5));

	vec4 pattern_cell_pos = vec4(float(iCol) , (float(iRow)), float(iRow)+0.5, (float(iCol) + 0.5));

	vec4 patternSize = vec4(patterWidth, patterHeight, patterHeight, patterWidth);

	vec4 movingEdge = (pattern_cell_pos * patternSize) + ((patternSize / 4.0) * (1.0 + sin(timePattern)));

	float ym = patterHeight * (float(iRow) + 0.5);
	float xm = patterWidth * (float(iCol) + 0.5);

	vec4 cc = vec4(0.6,0.8,0.9,0.0);
	 vec4 color = (1.0 + sin(cc * iTime)) / 2.0;


	vec4 color1 = color;
	vec4 color2 = color1;
	vec4 color3 = color1;
	vec4 color4 = color1;

	/*
	vec4 color1 = COLOR1;
	vec4 color2 = COLOR2;
	vec4 color3 = COLOR3;
	vec4 color4 = COLOR4;
	  */

	if (fragCoord.x < movingEdge.x && fragCoord.y > ym ){
		fragColor = color1;
	} else if (fragCoord.y < movingEdge.y  && fragCoord.x < xm ){
		fragColor = color2;
	} else if (fragCoord.x > movingEdge.w && fragCoord.y < ym){
		fragColor = color3;
	} else if (fragCoord.y > movingEdge.z && fragCoord.x > xm){
		fragColor = color4;
	} else {
		fragColor = WHITE_COLOR;
	}

}
