void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int mode = 3;


    // k from 0 to 1
    float k = (1.0 + sin(iTime)) / 2.0;
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 ruv = uv - vec2(0.5, 0.5);

    if (mode == 0)
    	uv = ruv / (k + k * (1.0 - k) * sin(uv.y * 3.0 + uv.x * 4.0)) + vec2(0.5, 0.5);
	if (mode == 1)
    	uv = ruv / (k + k * (1.0 - k) * sin(uv.y * 100.0 + uv.x * 100.0)) + vec2(0.5, 0.5);
    if (mode == 2)
    	uv = ruv / (k + k * (1.0 - k) * sin(uv.x * uv.y * 100.0*atan(uv.y, uv.x))) + vec2(0.5, 0.5);
    if (mode == 3)
	    uv = ruv / (k + k * (1.0 - k) * sin((1.0 + uv.x) * (1.0 + uv.y) * 100.0*atan(uv.y, uv.x))) + vec2(0.5, 0.5);

    if (uv.x > 1.0 || uv.y > 1.0 || uv.x < 0.0 || uv.y < 0.0)
        fragColor = vec4(0, 0, 0, 0);
    else
		fragColor = texture(iChannel0, uv);
}
