void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    vec2 mouse = iMouse.xy / iResolution.xy;
	mouse -= 0.5;

    float alt = sin(iTime) + .5 * .5;
	uv -= mouse;

    if (uv.y > 0. && uv.y < 0.04)

        fragColor = vec4(alt,(1.-alt),0.,1.);

    else if (uv.y > 0.07 && uv.y < 0.11)


        fragColor = vec4((1. - alt) ,alt, 0.,1.);

    else if (uv.y > 0.14 && uv.y < 0.18)

        fragColor = vec4(alt,(1.-alt),0.,1.);

    else if (uv.x > 0. && uv.x < 0.04)

        fragColor = vec4(alt,(1.-alt),0.,1.);

    else if (uv.x > 0.07 && uv.x < 0.11)

        fragColor = vec4((1. - alt) ,alt, 0.,1.);

    else if (uv.x > 0.14 && uv.x < 0.18)

        fragColor = vec4(alt,(1.-alt),0.,1.);

    else

        fragColor = vec4(alt);
}
