void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float lineWidth = 0.03;
    float data = texture(iChannel0, vec2(uv.x, 0.25)).x;
    vec3 c = vec3(0.0);
    if(uv.y > data-lineWidth && uv.y < data+lineWidth) {
        c = vec3(1.0, 1.0, 1.0);
    } else if(uv.y < 0.1) {
        float sum = 0.0;
        int count = 0;
        for(float i = 0.001; i < 0.04; i += 1.0/512.0) {
        	sum += texture(iChannel0, vec2(i, 0.25)).x;
            count++;
        }
        float average = sum/float(count);
    	c = vec3((average-0.5)*2.0, 0.0, 0.0);
    }
	fragColor = vec4(c, 1.0);
}
