#define SIZE 10.

float C,S;
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)

vec3 ro,rd;
float c;

void rays(vec3 p, vec3 d) {
    vec3 n=cross(rd,d);
    vec3 np=cross(rd,n),nq=cross(d,n);
    float t=dot(ro-p,np)/dot(d,np),
        r=dot(p-ro,nq)/dot(rd,nq);
    float u=t+SIZE*.5;
    float i=floor(u/SIZE);
    u=mod(u,SIZE)-SIZE*.5;
    u=sign(u)*min(abs(u),SIZE*.25);
    u=i*SIZE+u;
    vec3 h=p+d*u;
    float l=length(cross(rd,ro-h));
    c+=step(0.,r)*mix(.01,.005,sin(iTime*10.))/l/l*pow(max(1.-r*.032,0.),.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    c=0.;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    ro=vec3(-1.+sin(iTime*.3+2.)*.5,-1.+sin(iTime*.5+4.)*.5,iTime);
    rd=normalize(vec3(uv,1.));
    rd.xz*=rot(sin(iTime*.3));
    rd.xy*=rot(sin(iTime*.5));
    float o=floor(iTime/SIZE*2.)+6.;
    for(float x=-6.;x<6.;x+=1.)
    {
        for(float y=-6.;y<6.;y+=1.)
        {
            rays(vec3(.5,y,x+o)*SIZE*.5, vec3(1.,0.,0.));
            rays(vec3(y,.5,x+o)*SIZE*.5, vec3(0.,1.,0.));
            rays(vec3(x,y,.5)*SIZE*.5, vec3(0.,0.,1.));
        }
    }
    vec3 clr=mix(vec3(.1,.5,1.), vec3(.2,.6,1.),c)*c;
    clr=sqrt(clr);
	fragColor = vec4(clr,1.);
}
