void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(1.0);
    float GlobalTime = iTime * 3.0;
    r.y *= 1.5;

    ret = mix(ret, vec3(1.0, sin(GlobalTime) * sin(GlobalTime) * 0.7, sin(GlobalTime) * sin(GlobalTime) * 0.7), smoothstep(1.0, 2.0, length(r)));

	fragColor = vec4(ret,1.0);
}
