float border = 200.0;
vec2 pxl = vec2(50, 50);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	pxl = iMouse.yy / 4.0;
	border = iMouse.x;

	vec2 correctedPos = floor(fragCoord.xy / pxl) * pxl / iResolution.xy;
	vec4 resultClr = texture(iChannel0, correctedPos.xy);
	vec4 actualClr = texture(iChannel0, (fragCoord.xy / iResolution.xy));

	fragColor = mix(actualClr, resultClr, step(border, fragCoord.x));
}
