#define PI 3.14159265359

float F (float x, float y) {
	float N = 5.0;
	float r = distance (vec2 (x,y), vec2 (0.0));
	float f = mod (atan (y, x) + iTime * PI * 0.5, PI*2.0);
	float q = (f/PI + 1.0)/2.0;
	return (r*2.0/(1.0 - sin (f*N)) - 0.5); 	
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 
		(fragCoord.xy / iResolution.xy - vec2 (0.5))* 
		vec2 (iResolution.x / iResolution.y, 1.0) ;

	float f = F(uv.x, uv.y);

	if (f > 0.0)
		fragColor = vec4 (1.0,1.0 - f,0.0,1.0);
	else
		fragColor = vec4 (1.0 + f,1.0,0.0,1.0);			
}
