void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / (iResolution.xy * (sin(iTime) + 2.0));

	// first texture row is frequency data
	float fft  = texture( iChannel0, vec2(uv.x,0.15) ).x; 

    // second texture row is the sound wave
	float wave = texture( iChannel0, vec2(uv.x,0.75) ).x;

	// convert frequency to colors
	vec3 col = vec3( fft, 5.0*fft*(1.0-fft), 1.0-fft ) * fft;

    // add wave form on top	
	col += vec3(1.0 - smoothstep( 0.0, 0.2, uv.y - fft), 0.0, 0.0);

	// output final color
	fragColor = vec4(col,1.0);
}
