#define iterations 128

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (-1.0 + 2.0*fragCoord.xy / iResolution.xy) * vec2(iResolution.x/iResolution.y, 1.0);

	if(iMouse.z > 0.0) {
		//zooming in the burning ship from hell
		p *= 0.044;
		p.x -= 1.75;
		p.y += 0.037;
	} else {
		//zooming in the entire burning ship fractal
		p *= 1.0;
		p.x -= 0.3;
		p.y += 0.6;
	}

	//z & c declarations
	vec2 c = p;
	vec2 z = c;
	float i = 0.0;

	//iterate the pixel
	for (int j = 0; j < iterations; j++) {
		if (length(z) > 2.0) break;

		//burning-ship fractal equation
		float x = abs(z.x);
		float y = abs(z.y);
		z = vec2(x*x-y*y, -2.0*x*y) + c;
		i++;
	}

	//colors
	fragColor = vec4((length(z) > 2.0) ? mix(vec3(0.1, 0.0, 0.0), vec3(1.0, 0.3, 0.0), i/float(iterations)) : vec3(0.0), 1);
}
