float median( float x )
{
    return 1.6 * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rotation = sin(iTime);

    vec2 uv = fragCoord.xy / iResolution.xy;

    float kx = rotation;
    float ky = 1.0 - kx;

    float redness = uv.x * kx + uv.y * ky;

    vec4 tex = texture(iChannel0, uv);
    float avgColor = (tex.r + tex.b + tex.g) / 3.0;

    vec4 gradient = vec4(
        median(redness)
      , median(1.0 - avgColor)
      , median(1.0 - avgColor)
      , 1.0
    );

    fragColor = vec4(avgColor, avgColor, avgColor, 1.0) * gradient;
}
