void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 farve1 = vec3(uv,0.5+0.5*sin(iTime));

    vec3 farve2 = farve1 - vec3(1./4.);

    vec3 pixel = farve1;

    if (uv.x > 1./4. 
    &&  uv.x < 3./4. 
    &&  uv.y > 1./4. 
    &&  uv.y < 3./4.) {
        pixel = farve2;
    }    

    fragColor = vec4(clamp(pixel, 0.0, 1.0),1.0);

}
