renderpass.code:
mat2 z=mat2(1,1,1,-1);
float m(vec2 p)
{
    return step(cos(
        1.4257*(texture(iChannel0, 
                          floor(z*p*.1)
                          /64.,-32.).
                r >.5 ? p.y : p.x)),
                .17);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = z * (fragCoord.xy / iResolution.y * 25. 
                  + iMouse.xy*.1 
                  + iTime*vec2(1.5,1.08)),
        c = floor(p);
    float s = step(1. - p.x + c.x, p.y - c.y), 
        f = m(c), 
        g = m(c + vec2(-1, 0) + s);
    s/=4.;
    vec2 sz = vec2(0);
	fragColor.rgb = mix(vec3(.3,.3,.25),
                        vec3(1),
                        m(c-z[1])+(g < f ? 
                                   f*(.75-s) : 
                                   g*(.5+s)));
}
