// http://stackoverflow.com/questions/12964279/whats-the-origin-of-this-glsl-rand-one-liner
float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float unit = 2. / iResolution.y;


    vec2 offset = vec2(0,1) * unit * (1. + rand(uv.xx) * 10.);

    vec4 video = texture(iChannel1, uv);
    float chroma = clamp(video.g * 2. - video.r - video.g,0.,1.);
    video = mix(video, vec4(0,0,0,0), chroma);

    vec2 uvOffset = uv + offset;
    uvOffset = mod(abs(uvOffset),1.);
    vec4 buffer = texture(iChannel0, uvOffset);

    vec4 color = mix(video, buffer, step(0.1, chroma));
    fragColor = color;
}
