void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(1.0, 0.3, 0.3);

    float the = 0.0 + iTime * 0.3;
    vec2 s = r;
    s.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    if(length(r) <= 0.1){
    	ret = vec3(1.0);
    }
    else if(length(s) >= 0.3 && length(s) <= 0.32 && s.x <= 0.1){
    	ret = vec3(1.0);
    }

    the = 1.0 + iTime * 0.2;
    s.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    if(length(s) >= 0.45 && length(s) <= 0.47 && s.x >= 0.1){
    	ret = vec3(1.0);
    }

    the = 1.0 + iTime * 0.2;
    s.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    if(length(s) >= 0.6 && length(s) <= 0.62 && s.x <= -0.2){
    	ret = vec3(1.0);
    }

    the = 1.0 - iTime * 0.1;
    s.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    if(length(s) >= 0.75 && length(s) <= 0.77 && s.x >= -0.4){
    	ret = vec3(1.0);
    }

    vec2 t = r;

    the = iTime * 0.1 - 0.7;
    t.y *= 2.0;
    t.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    if(length(t) >= 1.0 && length(t) <= 1.02 && (t.y >= 0.7 || t.y <= -0.7)){
    	ret = vec3(1.0);
    }

	fragColor = vec4(ret,1.0);
}
