#define INCREMENT  2.2

#define GAMMA true

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float gamma = 1.0/INCREMENT;

	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = texture ( iChannel0, uv);



    if ( fragCoord.x > iResolution.x/2.0){


        if ( GAMMA){
   		 fragColor.xyz.rgb = vec3(pow(fragColor.xyz.r,INCREMENT),
                        pow(fragColor.xyz.g,INCREMENT),
                        pow(fragColor.xyz.b,INCREMENT)); 
        }else{

   	 	fragColor.xyz.rgb = vec3(pow(fragColor.xyz.r,gamma),
                        pow(fragColor.xyz.g,gamma),
                        pow(fragColor.xyz.b,gamma));

        }

    }


}
