#define PI  3.1415926
#define TPI (PI*2) 
#define HPI (PI/2)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float time=iTime;

    vec3 col;
    col.r = texture(iChannel0,vec2(uv.x+0.003,uv.y)).x;
    col.g = texture(iChannel0,vec2(uv.x+0.003,uv.y)).y;
    col.b = texture(iChannel0,vec2(uv.x+0.003*sin(2.0*time),uv.y)).z;   



  col = clamp(col*0.5+0.5*col*col*1.2,0.0,1.0);

    col *=0.9+0.5*abs(sin(10.0*time+uv.x*1000.0));
    col *=0.6+0.5*abs(sin(10.0*time+uv.y*1000.0));    

    col *= 0.98+0.03*sin(1.0*time);

    float comp = smoothstep( 0.2, 0.5, sin(time) );
    col = mix( col, col, clamp(-2.0+2.0*uv.x+3.0*comp,0.0,1.0) );




	fragColor = vec4(col,1.0);
}
