#define iTime sin(iTime*6.2)

#define DTM 78.0

#define D 8.0
#define T 35.0
#define M 35.0

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy*2.0-1.0;

    uv.x *= iResolution.x/iResolution.y;

    float scale = (sin(iTime)*.5+.5);

    float d;

    vec3 col = vec3(0.0);

    for(int i = 0; i < 33; i++)
    {
        vec2 p = vec2(cos(float(i)/33.0*PI*2.0+iTime), sin(float(i)/33.0*PI*2.0+iTime));

        d = length(uv-p)*0.7;


    	if(d <= 3.0)
        {
            col += (vec3(
            sin(-iTime*PI*2.0+D+d*PI),
            sin(-iTime*PI*2.0+4.0*PI/3.0+M+d*PI),
            sin(-iTime*PI*2.0+2.0*PI/2.0+T+d*PI))*.5+.5)*sin(d*PI*DTM+iTime*DTM);
        }
    }

    d = length(uv)*2.2;

    col += vec3(
    	sin(-iTime*PI*3.0+D+d*PI),
        sin(-iTime*PI*2.0+4.0*PI/3.0+M+d*PI),
        sin(-iTime*PI*3.0+3.0*PI/2.0+T+d*PI));

		fragColor = vec4(col, 1.0);
}


