#define HALF_PI 1.57079632679
#define PI      3.14159265359

float hash(float x)
{
    return fract(sin(x) * 43758.5453) * 2.0 - 1.0;
}

float sineInOut(float t)
{
  return -0.5 * (cos(PI * t) - 1.0);
}

const float speed = 1.00;
const float duration = 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2  uv = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;   

    float sequence = floor(iTime / duration);
    float current_time = mod(iTime, duration);
    float start = -1.0;
    float end   = 1.0;
    float pos = 0.0;      
    float dist = speed * current_time;
    float goal = abs(end - start);

    if (dist <= goal) 
    {
        pos = mix(start, end, sineInOut(dist / goal));
    } 
    else 
    {
        pos = end;
    }

    float f = step(uv.y, pos);

    fragColor = vec4(f);
}
